.tc-tiddler-frame {
  border: none;
  border-bottom: 1px solid <<color page-background>>;
  border-radius: 0;
  box-shadow: none;
  margin: 0;
  flex-shrink: 0;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  position: sticky;
}

.tc-alerts + p {
  margin: 0;
}

@media (min-width: 960px) {
  .tc-dropzone {
    height: 100vh;
    display: flex;
    flex-direction: column;
  }

  .tc-story-river {
    padding: 0;
    display: flex;
    overflow-y: hidden;
    height: 100%;
  }

  /**
   * Fix for Firefox and Safari where sticky elements (tiddlers) cause different scrollWidth of parent element (story river).
   */
  .tc-story-river::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: calc(var(--tiddler-width) * var(--tiddler-count));
    z-index: -1;
  }

  .krystal--static .tc-story-river {
    max-height: 100vh;
  }

  .tc-tiddler-frame {
    border: none;
    border-right: 1px solid <<color page-background>>;
    padding: var(--tiddler-padding);
    width: var(--tiddler-width);
  }

  .tc-tiddler-frame > * {
    transition: opacity 0.25s ease;
  }

  .krystal-link--active {
    background: <<color message-background>>;
  }

  .krystal-tiddler__frame--hide > *:not(.krystal-tiddler__title) {
    opacity: 0;
  }

  .krystal-tiddler__frame--overlay {
    box-shadow: 0 0 1em rgba(0, 0, 0, 0.125);
  }

  .krystal-tiddler__frame--maximized {
    left: 0 !important;
    right: 0 !important;
    bottom: 0;
    width: 100%;
    z-index: 5;
  }

  .tc-tiddler-frame:nth-of-type(1) { left: calc(0 * var(--tiddler-padding)) }
  .tc-tiddler-frame:nth-of-type(2) { left: calc(1 * var(--tiddler-padding)) }
  .tc-tiddler-frame:nth-of-type(3) { left: calc(2 * var(--tiddler-padding)) }
  .tc-tiddler-frame:nth-of-type(4) { left: calc(3 * var(--tiddler-padding)) }
  .tc-tiddler-frame:nth-of-type(5) { left: calc(4 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(6) { left: calc(5 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(7) { left: calc(6 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(8) { left: calc(7 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(9) { left: calc(8 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(10) { left: calc(9 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(11) { left: calc(10 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(12) { left: calc(11 * var(--tiddler-padding)); }
  .tc-tiddler-frame:nth-of-type(13) { left: calc(12 * var(--tiddler-padding)); }
